import React, {Fragment, useState, useEffect, Component} from 'react';
import logo from './logo.svg';
import Web3 from 'web3';
import SupportChildren from "./abis/SupportChildren.json";
import './App.css';

import InputCampaign from './components/InputCampaign';
import ListCampaign from './components/ListCampaign';

function App(){

  const [contract, setContract] = useState(null);
  const [account, setAccount] = useState('');

  const componentWillMount = async () => {
    await loadWeb3();
    await loadBlockchainData();
  } 

  const loadWeb3 = async () => {
    if(window.ethereum){
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }else if(window.web3){
      window.web3 = new Web3(window.web3.currentProvider)
    }else{
      window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
    }
  }

  const loadBlockchainData = async () => {
    const web3 = window.web3
    // Load account
    const accounts = await web3.eth.getAccounts()
    await setAccount(accounts);

    const networkId = await web3.eth.net.getId()
    const networkData = await SupportChildren.networks[4]
    if(networkData) {
        const abi = SupportChildren.abi
        const address = networkData.address
        const contract = new web3.eth.Contract(abi, address)

        await setContract(contract);
        console.log(this.contract)
    } else {
        window.alert('Smart contract not deployed to detected network')
    }
  }

  useEffect(() => {
    componentWillMount();
  }, [])

    return (
      <Fragment>
        <div className = "container">
          <InputCampaign contract = {contract} account = {account}/>
          <ListCampaign contract = {contract} account = {account}/>
        </div>
      </Fragment>
    );
}

export default App;
