import React, {Fragment, Component} from 'react';
import logo from '../logo.svg';
import Web3 from 'web3';
import SupportChildren from "../abis/SupportChildren.json";
import '../App.css';

class CampaignView extends Component {

    constructor(props){
        super(props)
        this.state = {
          contract: null,
          account: '',
          campaign: '',
          amountETH: '',
          amountERC: '',
          email: ''
        }
      }
    
      async componentWillMount() {
        await this.getCampaign();
        await this.loadWeb3();
        await this.loadBlockchainData();
      } 
    
      async loadWeb3(){
        if(window.ethereum){
          window.web3 = new Web3(window.ethereum)
          await window.ethereum.enable()
        }else if(window.web3){
          window.web3 = new Web3(window.web3.currentProvider)
        }else{
          window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
        }
      }
    
      async loadBlockchainData() {
        const web3 = window.web3
        // Load account
        const accountWeb3 = await web3.eth.getAccounts()
        this.setState({account: accountWeb3[0]})

        const addressField = document.getElementById("address")
        addressField.innerHTML = accountWeb3[0]
    
        const networkId = await web3.eth.net.getId()
        const networkData = await SupportChildren.networks[networkId]
        if(networkData) {
            const abi = SupportChildren.abi
            const address = networkData.address
            const contractWeb3 = new web3.eth.Contract(abi, address)
    
            this.setState({contract: contractWeb3})
    
        } else {
            window.alert('Smart contract not deployed to detected network')
        }
      }

      async getCampaign () {
        try {
            const lastItem = window.location.pathname.split("/").pop()
            const response = await fetch("http://localhost:5000/campaigns/" + lastItem)
            const jsonData = await response.json()

            this.setState({
                campaign: jsonData
            })

        } catch (err) {
            console.log(err.message);
        }
    }

    //Donate functions

    donateETH = (evt) =>{
        evt.preventDefault();

        console.log(this.state.campaign.camp_raised + this.state.amountETH)

        if(parseFloat(this.state.campaign.camp_raised) + parseFloat(this.state.amountETH) > parseFloat(this.state.campaign.camp_goal)){
          alert(`The campaign limit is ${this.state.campaign.camp_goal}`)
          return;
        }

        this.state.contract.methods.donateETH(parseInt(this.state.campaign.camp_id)-1, this.state.email)
        .send({from: this.state.account, value: parseFloat(this.state.amountETH)*(10**18)})
        .then(async () =>{
            try {
                const body = {raised : (this.state.campaign.camp_raised + parseFloat(this.state.amountETH))};
                const response = await fetch(`http://localhost:5000/campaigns/${this.state.campaign.camp_id}`, {
                    method: "PUT",
                    headers: {"Content-Type" : "application/json"},
                    body: JSON.stringify(body)
                });
                const lastItem = window.location.pathname.split("/").pop()
                // window.location = "/campaign/" + this.state.campaign.camp_id;
            } catch (err) {
                console.log(err.message);
            }
        }).then(async () => {
          console.log("usao sam")
          if(parseFloat(this.state.campaign.camp_raised) + parseFloat(this.state.amountETH) == parseFloat(this.state.campaign.camp_goal)){
            try {
              const body = {isFinished: Boolean(true)}
              const response = await fetch(`http://localhost:5000/campaigns/${this.state.campaign.camp_id}/finish`, {
                method: "PUT",
                headers: {"Content-Type" : "application/json"},
                body: JSON.stringify(body)
              });
            } catch (err) {
              console.log(err.message)
            }
          }
          
        })
    }


      render(){
          return(
            <Fragment>
                <div className="dist1">
                  <div className="middle">
                    <div className="title">
                      <a>Title of the campaign</a>
                    </div>
                    <div className="mainsell">
                      {/* <img className="img" src={image15} alt="" /> */}
                    </div>
                    <div className="buttonsfraime">
                  <ol>
                      <div className="time left">
                        <div className="timetext">Time left</div>
                        <div className="timeclock">
                          <a>50 days 20 hours 38 minutes</a>
                        </div>
                      </div>
                      <div className="buttonPos">
                        <button className="buttonfund" type="button" onclick="#">Donate Now</button>
                      </div>
                      <div>
                        <progress id="file" value="32" max="100"></progress>
                      </div>
                      <div>
                        <label>2813</label><a> out of </a><label>81923</label>
                      </div>
                      <div className="lasttopdon">
                        <a>Last donation was </a>
                        <a>3 days ago</a>
                      </div>
                      <div>
                        <div className="lasttopdon" style={{fontSize:'22px'}}>Top donators</div>
                        <div className='listofdon'>
                          <a>Metamask</a>
                          <a style={{float:'right'}}>Amount</a>
                        </div>
                        <div className='listofdon'>
                          <a>Metamask</a>
                          <a style={{float:'right'}}>Amount</a>
                        </div>
                        <div className='listofdon'>
                          <a>Metamask</a>
                          <a style={{float:'right'}}>Amount</a>
                        </div>
                        <div className="dono">
                          <button className="seealldonators" type="button"  onclick="#">See all donators</button>
                        </div>
                      </div>
                  
                    </ol>
                  
                </div>
