import React, {Fragment, Component} from 'react';
import Web3 from 'web3';
import { MaticPOSClient } from '@maticnetwork/maticjs'
import WalletConnectProvider from "@maticnetwork/walletconnect-provider";

const config = require("../config");


class L2View extends Component {

    constructor(props){
        super(props);
        this.state = {
            Networkid: 0,
            account: "",
            inputValue: "",
            burnHash: "",
            loading: true

        }
    }

    // posClientGeneral facilitates the operations like approve, deposit, exit

    posClientParent = () => {
        const maticPoSClient = new MaticPoSClient({
          network: config.l2.NETWORK,
          version: config.l2.VERSION,
          maticProvider: maticProvider,
          parentProvider: window.web3,
          parentDefaultOptions: { from: this.state.account },
          maticDefaultOptions: { from: this.state.account },
        });
        return maticPoSClient;
    }

    async componentWillMount() {
        await this.loadWeb3();
        await this.loadBlockchainData();
    }

    loadWeb3 = async () => {
        if (window.ethereum) {
          window.web3 = new Web3(window.ethereum);
          await window.ethereum.enable();
        } else if (window.web3) {
          window.web3 = new Web3(window.web3.currentProvider);
        } else {
          window.alert(
            "Non-Ethereum browser detected. You should consider trying MetaMask!"
          );
        }
    }

    loadBlockchainData = async () => {

        this.setState({
            loading: false
        })

        const maticProvider = new WalletConnectProvider({
          host: config.l2.MATIC_RPC,
          callbacks: {
            onConnect: console.log("matic connected"),
            onDisconnect: console.log("matic disconnected!"),
          },
        });
    
        const ethereumProvider = new WalletConnectProvider({
          host: config.l2.ETHEREUM_RPC,
          callbacks: {
            onConnect: console.log("mainchain connected"),
            onDisconnect: console.log("mainchain disconnected"),
          },
        });
        
        this.setState({
            maticProvider: maticProvider,
            ethereumProvider: ethereumProvider
        })

        const web3 = window.web3;
    
        const accounts = await web3.eth.getAccounts();
        this.setState({account: accounts[0]});

        const networkId = await web3.eth.net.getId();
        this.setState({networkId: networkId});
    
        if (networkId === config.l2.ETHEREUM_CHAINID) {
            this.setState({loading: false});
        } else if (networkId === config.l2.MATIC_CHAINID) {
            this.setState({loading: false});
        } else {
          window.alert(" switch to  Matic or Ethereum network");
        }
    }

    depositEther = async () => {
        const maticPoSClient = posClientParent();
        const x = this.state.inputValue * 1000000000000000000; // 18 decimals
        const x1 = x.toString();
    
        await maticPoSClient.depositEtherForUser(this.state.account, x1, {
          from: this.state.account,
        });
    }

    burnEther = async () => {
        const maticPoSClient = posClientChild();
        const x = this.state.inputValue * 1000000000000000000;
        const x1 = x.toString();
        await maticPoSClient
          .burnERC20(config.l2.posWETH, x1, {
            from: this.state.account,
          })
          .then((res) => {
            console.log(res.transactionHash);
            this.setState({transactionHash: res.transactionHash});
          });
    }

    exitEther = async () => {
        const maticPoSClient = posClientParent();
        await maticPoSClient
          .exitERC20(this.state.inputValue, {
            from: this.state.account,
          })
          .then((res) => {
            console.log("exit o/p", res);
          });
    }

    // POS ERC20 functionality

    depositERC20 = async () => {
        const maticPoSClient = posClientParent();
        const x = this.state.inputValue * 1000000000000000000; // 18 decimals
        const x1 = x.toString();
        await maticPoSClient.approveERC20ForDeposit(config.l2.posRootERC20, x1, {
            from: this.state.account,
        });
        await maticPoSClient.depositERC20ForUser(config.l2.posRootERC20, this.state.account, x1, {
            from: this.state.account,
        });
    };

    burnERC20 = async () => {
        const maticPoSClient = posClientChild();
        const x = this.state.inputValue * 1000000000000000000;
        const x1 = x.toString();
        await maticPoSClient
        .burnERC20(config.l2.posChildERC20, x1, {
            from: this.state.account,
        })
        .then((res) => {
            this.setState({transactionHash: res.transactionHash});
        });
    }

    exitERC20 = async () => {
        const maticPoSClient = posClientParent();
        await maticPoSClient
        .exitERC20(this.state.inputValue, {
            from: this.state.account,
        })
        .then((res) => {
            console.log("exit o/p", res);
        });
    }

    onchange = (e) => {
        this.setState({inputValue: e.target.value});
    }

    render(){
        return(
            <div
            id="Ether"
            >
            <button
              onClick={this.depositEther}
              disabled={
                this.state.Networkid !== 0 && this.state.Networkid === config.l2.MATIC_CHAINID
                  ? true
                  : false
              }
            >
              Deposit
            </button>

            <button
              onClick={this.burnEther}
              disabled={
                this.state.Networkid !== 0 && this.state.Networkid === config.l2.ETHEREUM_CHAINID
                  ? true
                  : false
              }
            >
              burn
            </button>

            <button
              onClick={this.exitEther}
              disabled={
                this.state.Networkid !== 0 && this.state.Networkid === config.l2.ETHEREUM_CHAINID
                  ? false
                  : true
              }
            >
              exit
            </button>

            <br />
            <input
              id="inputValue"
              type="text"
              placeholder="value"
              name="inputValue"
              value={this.state.inputValue}
              onChange={this.onchange}
              required
            />
            <p id="burnHash">{this.state.burnHash}</p>
          </div>
        );
    }

}

export default L2View;
