import React, {Fragment, Component} from 'react';
import DonateCampaign from './DonateCampaign';
import Web3 from 'web3';
import SupportChildren from "../abis/SupportChildren.json";


class ListCampaign extends Component  {

    constructor(props){
        super(props);
        this.state = {
            contract: null,
            account : '',
            campaigns: []
        }
    }

    async componentWillMount() {
        await this.getCampaigns()
        await this.loadWeb3();
        await this.loadBlockchainData();
      } 

    async getCampaigns () {
        try {
            const response = await fetch("http://localhost:5000/campaigns")
            const jsonData = await response.json()

            this.setState({
                campaigns: jsonData
            })

        } catch (err) {
            console.log(err.message);
        }
    }

    async loadWeb3(){
        if(window.ethereum){
          window.web3 = new Web3(window.ethereum)
          await window.ethereum.enable()
        }else if(window.web3){
          window.web3 = new Web3(window.web3.currentProvider)
        }else{
          window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
        }
      }
    
    async loadBlockchainData() {
        const web3 = window.web3
        // Load account
        const accountWeb3 = await web3.eth.getAccounts()
        this.setState({account: accountWeb3[0]})
    
        const networkId = await web3.eth.net.getId()
        const networkData = await SupportChildren.networks[networkId]
        if(networkData) {
            const abi = SupportChildren.abi
            const address = networkData.address
            const contractWeb3 = new web3.eth.Contract(abi, address)
    
            this.setState({contract: contractWeb3})
    
        } else {
            window.alert('Smart contract not deployed to detected network')
        }
    }
    

    render(){
        return(
            <Fragment>
                <container className="mt-5 text-center d-flex">
                    {this.state.campaigns.map(campaign => (
                        // <tr key = {campaign.camp_id}>
                        //     <td>{campaign.camp_title}</td>
                        //     <td>{campaign.camp_description}</td>
                        //     <td>{campaign.camp_email}</td>
                        //     <td>{campaign.camp_url}</td>
                        //     <td>{campaign.camp_datecreated}</td>
                        //     <td>{campaign.camp_deadline}</td>
                        //     <td>{campaign.camp_raised}</td>
                        //     <td>{campaign.camp_goal}</td>
                        //     <td><DonateCampaign camp = {campaign}/></td>
                        // </tr>
                        <div className = "card">
                            <p>{campaign.camp_title}</p>
                        </div>
                    ))}
                </container>
            </Fragment>
        )
    }
}

export default ListCampaign;
