import React, {Fragment, useEffect, useState} from 'react';
import DonateCampaign from './DonateCampaign';

const ListCampaign = (contract, account) => {

    const [campaigns, setCampaigns] = useState([]);

    const getCampaigns = async () => {
        try {
            const response = await fetch("http://localhost:5000/campaigns")
            const jsonData = await response.json()

            setCampaigns(jsonData);
            console.log(contract)
            console.log(account)
        } catch (err) {
            console.log(err.message);
        }
    }

    useEffect(() => {
        getCampaigns();
    },[])

    const createKampanja = () =>{
        contract.methods.createCampaign(account, 1728008037, "nesto").send({from: account})
    }

    return(
        <Fragment>
            <button onClick = {createKampanja}>list kampanje</button>
            <table className="table mt-5 text-center">
                <thead>
                <tr>
                    <th>Title</th>
                    <th>Description</th>
                    <th>Email</th>
                    <th>url</th>
                    <th>Date created</th>
                    <th>Deadline</th>
                    <th>Raised</th>
                    <th>Goal</th>
                    <th>Donate</th>
                </tr>
                </thead>
                <tbody>
                {campaigns.map(campaign => (
                    <tr key = {campaign.camp_id}>
                        <td>{campaign.camp_title}</td>
                        <td>{campaign.camp_description}</td>
                        <td>{campaign.camp_email}</td>
                        <td>{campaign.camp_url}</td>
                        <td>{campaign.camp_datecreated}</td>
                        <td>{campaign.camp_deadline}</td>
                        <td>{campaign.camp_raised}</td>
                        <td>{campaign.camp_goal}</td>
                        <td><DonateCampaign camp = {campaign}/></td>
                    </tr>
                ))}
                </tbody>
            </table>
        </Fragment>
    )
}

export default ListCampaign;
