import React, {Fragment, Component} from 'react';
import logo from '../logo.svg';
import Web3 from 'web3';
import SupportChildren from "../abis/SupportChildren.json";
import '../App.css';

class CampaignView extends Component {

    constructor(props){
        super(props)
        this.state = {
          contract: null,
          account: '',
          campaign: '',
          amountETH: '',
          amountERC: ''
        }
      }
    
      async componentWillMount() {
        await this.getCampaign();
        await this.loadWeb3();
        await this.loadBlockchainData();
      } 
    
      async loadWeb3(){
        if(window.ethereum){
          window.web3 = new Web3(window.ethereum)
          await window.ethereum.enable()
        }else if(window.web3){
          window.web3 = new Web3(window.web3.currentProvider)
        }else{
          window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
        }
      }
    
      async loadBlockchainData() {
        const web3 = window.web3
        // Load account
        const accountWeb3 = await web3.eth.getAccounts()
        this.setState({account: accountWeb3[0]})

        const addressField = document.getElementById("address")
        addressField.innerHTML = accountWeb3[0]
    
        const networkId = await web3.eth.net.getId()
        const networkData = await SupportChildren.networks[networkId]
        if(networkData) {
            const abi = SupportChildren.abi
            const address = networkData.address
            const contractWeb3 = new web3.eth.Contract(abi, address)
    
            this.setState({contract: contractWeb3})
    
        } else {
            window.alert('Smart contract not deployed to detected network')
        }
      }

      async getCampaign () {
        try {
            const lastItem = window.location.pathname.split("/").pop()
            const response = await fetch("http://localhost:5000/campaigns/" + lastItem)
            const jsonData = await response.json()

            this.setState({
                campaign: jsonData
            })

        } catch (err) {
            console.log(err.message);
        }
    }

    //Donate functions

    donateETH = (evt) =>{
        evt.preventDefault();

        if(this.state.campaign.camp_raised + this.state.amountETH > this.state.campaign.camp_goal){
          alert(`The campaign limit is ${this.state.campaign.camp_goal}`)
          return;
        }

        this.state.contract.methods.donateETH(parseInt(this.state.campaign.camp_id)-1)
        .send({from: this.state.account, value: parseFloat(this.state.amountETH)*(10**18)})
        .then(async () =>{
            try {
                const body = {raised : (this.state.campaign.camp_raised + parseFloat(this.state.amountETH))};
                const response = await fetch(`http://localhost:5000/campaigns/${this.state.campaign.camp_id}`, {
                    method: "PUT",
                    headers: {"Content-Type" : "application/json"},
                    body: JSON.stringify(body)
                });
                const lastItem = window.location.pathname.split("/").pop()
                window.location = "/campaign/" + this.state.campaign.camp_id;
            } catch (err) {
                console.log(err.message);
            }
        })
    }

      render(){
          return(
            <Fragment>
                <p>{this.state.campaign.camp_title}</p>
                <h3>Forma za eth</h3>
                <form>
                    <p>Unesi eth iznos</p>
                    <input type = "number" className = "form-control" style = {{width: '20%'}} step="0.0000001" onChange = {evt => this.setState({amountETH: evt.target.value})}/>
                    <button className = "btn btn-primary" onClick = {evt => this.donateETH(evt)}>Donate</button>
                </form>

                <h3>Forma za erc20</h3>
                <form>
                    <p>Unesi adresu erc20 tokena</p>
                    <input type = "text" className = "form-control" style = {{width: '20%'}}/>
                    <p>Unesi iznos</p>
                    <input type = "number" className = "form-control" style = {{width: '20%'}} step="0.0000001"/>
                    <button className = "btn btn-success">Donate ERC20</button>
                </form>
            </Fragment>
          )
      }

}
export default CampaignView;
