import React, {Fragment, Component} from 'react';
import DonateCampaign from './DonateCampaign';
import Web3 from 'web3';
import SupportChildren from "../abis/SupportChildren.json";


class ListCampaign extends Component  {

    constructor(props){
        super(props);
        this.toggleListReverse = this.toggleListReverse.bind(this)
        this.toggleSortDate = this.toggleSortDate.bind(this)
        this.state = {
            contract: null,
            account : '',
            searchTerm: '',
            isOldestFirst: true,
            campaigns: []
        }
    }

    async componentWillMount() {
        await this.getCampaigns()
        await this.loadWeb3();
        await this.loadBlockchainData();
      } 

    async getCampaigns () {
        try {
            const response = await fetch("http://localhost:5000/campaigns")
            const jsonData = await response.json()

            this.setState({
                campaigns: jsonData
            })

        } catch (err) {
            console.log(err.message);
        }
    }

    async loadWeb3(){
        if(window.ethereum){
          window.web3 = new Web3(window.ethereum)
          await window.ethereum.enable()
        }else if(window.web3){
          window.web3 = new Web3(window.web3.currentProvider)
        }else{
          window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
        }
      }
    
    async loadBlockchainData() {
        const web3 = window.web3
        // Load account
        const accountWeb3 = await web3.eth.getAccounts()
        this.setState({account: accountWeb3[0]})
    
        const networkId = await web3.eth.net.getId()
        const networkData = await SupportChildren.networks[networkId]
        if(networkData) {
            const abi = SupportChildren.abi
            const address = networkData.address
            const contractWeb3 = new web3.eth.Contract(abi, address)

            this.setState({contract: contractWeb3})
        } else {
            window.alert('Smart contract not deployed to detected network')
        }
    }

    sortByDate () {
        const {campaigns} = this.state
        let newPostList = campaigns
        if (this.state.isOldestFirst) {
            console.log()
          newPostList = campaigns.sort((a, b) => Date.parse(a.camp_datecreated) - Date.parse(b.camp_datecreated))
        } else {
          newPostList = campaigns.sort((a, b) =>  Date.parse(a.camp_datecreated) - Date.parse(b.camp_datecreated))
        }
        this.setState({
          isOldestFirst: !this.state.isOldestFirst,
          campaigns: newPostList
        })
    }
    
    toggleSortDate (event) {
        this.sortByDate()
    }



    render(){
        return(
            <Fragment>
                <div>
                    <div>
                        <input type = "text" placeholder = "Search..." onChange = {evt => this.setState({searchTerm: evt.target.value})} />
                        <button onClick={this.toggleSortDate}>Order by date</button>
                    </div>
                    <div classname = "container mt-5 text-center d-flex">
                    {this.state.campaigns.filter((campaign) => {
                        if(this.state.searchTerm == ""){
                            return campaign;
                        }else if(campaign.camp_title.toLowerCase().includes(this.state.searchTerm.toLowerCase())){
                            return campaign;
                        }
                    }).map(campaign => (
                        <div className = "card" id = {campaign.camp_id} onClick = {function(){window.location.href = "/campaign/" + campaign.camp_id}} key = {campaign.camp_id}>
                            <p>{campaign.camp_title}</p>
                            <p>{campaign.camp_raised}</p>
                        </div>
                    ))}
                    </div>
                </div>
            </Fragment>
        )
    }
}

export default ListCampaign;
