import React, {Fragment, Component} from 'react';

//@author Luka

class InputCampaign extends Component{

    constructor(props){
        super(props);
        this.state = {
            title: '',
            description: '',
            email: '',
            url: '',
            deadline: '',
            goal: ''
        }
    }

    onSubmitForm = async (evt) => {
        evt.preventDefault();
        try {
            const body = {
                title: this.state.title,
                description: this.state.description, 
                email: this.state.email, 
                url: this.state.url, 
                deadline: this.state.deadline, 
                goal: this.state.goal
            };
            console.log(body);
            const response = await fetch("http://localhost:5000/create-campaign", {
                method: "POST",
                headers: {"Content-Type": "application/json"},
                body: JSON.stringify(body)
            });

            window.location = "/";
        } catch (error) {
            console.log(error.message);
        }
    }
    render(){
        return(
            <Fragment>
                <h1 className = "text-center mt-5">Create a campaign</h1>
                <form className = "d-block mt-5" onSubmit = {onSubmitForm}>
                    <h3>Title</h3>
                    <input type = "text" className = "form-control" value = {this.state.title} onChange = {evt => setTitle(evt.target.value)}/>
                    <h3>Description</h3>
                    <input type = "text" className = "form-control" value = {this.state.description} onChange = {evt => setDescription(evt.target.value)}/>
                    <h3>Email</h3>
                    <input type = "text" className = "form-control" value = {this.state.email} onChange = {evt => setEmail(evt.target.value)}/> 
                    <h3>Deadline</h3>
                    <input type = "date" className = "form-control" value = {this.state.deadline} onChange = {evt => setDeadline(evt.target.value)}/>
                    <h3>Goal</h3>
                    <input type = "number" className = "form-control" value = {this.state.goal} onChange = {evt => setGoal(evt.target.value)}/> 
                    <button className = "btn btn-success">Add</button>
                </form>
            </Fragment>
        )
    }
};

export default InputCampaign;
