import React, { Component } from 'react';
import Web3 from 'web3';
import SupportChildren from "../abis/SupportChildren.json";
import '../App.css';
import profimg from '../images/profileimage.png';
import donoapp from '../images/donoapp.png';
import { Line } from 'react-chartjs-2';
import {NavLink} from 'react-router-dom';
import Moment from 'moment';


class UserDashBoard extends Component{

    constructor(props){
        super(props)
        this.state = {
            contract: null,
            account: '',
            campaigns: [],
            campaignsBC: [],
            labels: [],
            data: []
        }
    }

    async componentWillMount() {
        await this.loadWeb3();
        await this.loadBlockchainData();
        await this.getCampaigns();
    } 

    async getCampaigns () {
        try {
          const response = await fetch("http://localhost:5000/campaigns")
          const jsonData = await response.json()
    
          this.setState({
            campaigns: jsonData.slice(0, 3)
          })
    
        } catch (err) {
          console.log(err.message);
        }
    }
    
      
    
    async loadWeb3(){
        if(window.ethereum){
          window.web3 = new Web3(window.ethereum)
          await window.ethereum.enable()
        }else if(window.web3){
          window.web3 = new Web3(window.web3.currentProvider)
        }else{
          window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
        }
    }
    
    async loadBlockchainData() {
        const web3 = window.web3
        // Load account
        const accountWeb3 = await web3.eth.getAccounts()
        this.setState({account: accountWeb3[0]})

        const addressField = document.getElementById("address")
        addressField.innerHTML = accountWeb3[0]
    
        const networkId = await web3.eth.net.getId()
        const networkData = await SupportChildren.networks[networkId]
        if(networkData) {
            const abi = SupportChildren.abi
            const address = networkData.address
            const contractWeb3 = new web3.eth.Contract(abi, address)
    
            this.setState({contract: contractWeb3})

            const campaignsBC = await contractWeb3.methods.getCampaigns().call()
            const indices = []

            for(var i =0;i<campaignsBC.length;i++){
              if(campaignsBC[i].beneficiary == this.state.account){
                indices.push(i);
              }
            }

            const labels = []
            const data = []

            for(var i =0;i<this.state.campaigns.length;i++){
              console.log(this.state.campaigns[i].camp_id)
              if(campaignsBC.includes(parseInt(this.state.campaigns[i].camp_id)-1)){
                labels.push(this.state.campaigns[i].camp_ttle);
                data.push(this.state.campaigns[i].camp_raised);
              }
            }

            console.log(data)

            this.setState({
              campaignsBC:indices,
              labels: labels,
              data: data
            })
    
        } else {
            window.alert('Smart contract not deployed to detected network')
        }
    }


    render() {
        return (
            <div>
              <title>My profile</title>
              <div className="dist1">
                <div className = "content" style={{paddingLeft: "300px", paddingRight: "300px"}}>
                  <div className="heading ">
                    <a>Donations</a>
                    <NavLink to="/create-campaign" id = "random"><button className = "buttonfund">Create fundraise</button></NavLink>
                  </div>
                  <div>
                  <Line
                    data = {{
                        labels: this.state.labels,
                        datasets: [
                            {
                                label: 'Amount that campaigns have raised',
                                data: this.state.data,
                                borderColor: 'Green',
                                tension: 0.1, /* Koliko je ostra linija promeniti po potrebi */
                            }
                        ]
                    }}
                    height = {300}
                    width = {600}
                    options = {{
                        maintainAspectRatio: false,
                        scales: {
                            yAxes: [{
                                ticks: {
                                    beginAtZero: true
                                }

                            }]
                        }
                    }}
                />
                  </div>
                  <div className="heading">
                    <a>Fundraise</a>
                  </div>
                  <div className="acinac">
                    <a>Active</a>
                    <a className="breck">(0)</a>
                  </div>
                  <div className = "row">
                    <div className = "col-md-4 col-sm-6">
                      {this.state.campaigns.filter((campaign) => {
                          console.log(campaign.camp_id)
                          if(this.state.campaignsBC.includes(parseInt(campaign.camp_id)-1)){
                            return campaign;
                          }
                      }).map(campaign => (
                        <div className = "card" id = {campaign.camp_id} onClick = {function(){window.location.href = "/campaign/" + campaign.camp_id}} key = {campaign.camp_id}>
                        <div className = "card-img-top">
                          <img src = "https://i.imgur.com/Jy7dSb.png"></img>
                        </div>
                        <div className = "card-body">
                          <p className = "card-title">{campaign.camp_title}</p>
                          <p className = "card-text">{campaign.camp_description}</p>
                          <p style={{color : "#959595", marginBottom: "1px"}}>Created {Moment(campaign.camp_deadline).format('L')}</p>
                          <progress value = {campaign.camp_raised} max = {campaign.camp_goal} className = "progress"></progress>

                          <p className = "card-text"><strong>Donated {campaign.camp_raised}</strong> of {campaign.camp_goal}</p>
                        </div>
                        </div>
                      ))}
                    </div>
                  </div>
                  <div className="acinac">
                    <a>Inactive</a>
                    <a className="breck">(0)</a>
                  </div>
                  <div className="nekosranje" />
                </div>
              </div>
            </div>
          );
    }



}

export default UserDashBoard;
