import React, {Fragment, Component} from 'react';
import Web3 from 'web3';
import SupportChildren from "../abis/SupportChildren.json";


//@author Luka

class InputCampaign extends Component{

    constructor(props){
        super(props);
        this.state = {
            contract: null,
            account: '',
            title: '',
            description: '',
            email: '',
            url: '',
            deadline: '',
            goal: '',
            selectionModel: ''
        }
    }
    
    async componentWillMount() {
        await this.loadWeb3();
        await this.loadBlockchainData();
    } 
    
    async loadWeb3(){
        if(window.ethereum){
          window.web3 = new Web3(window.ethereum)
          await window.ethereum.enable()
        }else if(window.web3){
          window.web3 = new Web3(window.web3.currentProvider)
        }else{
          window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
        }
    }
    
    async loadBlockchainData() {
        const web3 = window.web3
        // Load account
        const accountWeb3 = await web3.eth.getAccounts()
        this.setState({account: accountWeb3[0]})
    
        const networkId = await web3.eth.net.getId()
        const networkData = await SupportChildren.networks[networkId]
        if(networkData) {
            const abi = SupportChildren.abi
            const address = networkData.address
            const contractWeb3 = new web3.eth.Contract(abi, address)
    
            this.setState({contract: contractWeb3})
    
        } else {
            window.alert('Smart contract not deployed to detected network')
        }
    }

    onSubmitForm = async (evt) => {
        evt.preventDefault();
        this.state.contract.methods.createCampaign(this.state.account, 1928010725, "uri link").send({from: this.state.account})
        .then( async () => {
                try {
                const body = {
                    title: this.state.title,
                    description: this.state.description, 
                    email: this.state.email, 
                    url: this.state.url, 
                    deadline: this.state.deadline, 
                    goal: this.state.goal
                };
                console.log(body);
                const response = await fetch("http://localhost:5000/create-campaign", {
                    method: "POST",
                    headers: {"Content-Type": "application/json"},
                    body: JSON.stringify(body)
                });

                window.location = "/create-campaign";
            } catch (error) {
                console.log(error.message);
            }
        })
        
    }
    render(){
        return(
            <Fragment>
                <h1 className = "text-center mt-5">Create a campaign</h1>
                <form className = "d-block mt-5" onSubmit = {this.onSubmitForm} style = {{width: "20%"}}>
                    <h3>Title</h3>
                    <input type = "text" className = "form-control" value = {this.state.title} onChange = {evt => this.setState({title: evt.target.value})}/>
                    <h3>Description</h3>
                    <input type = "text" className = "form-control" value = {this.state.description} onChange = {evt => this.setState({description: evt.target.value})}/>
                    <h3>Email</h3>
                    <input type = "text" className = "form-control" value = {this.state.email} onChange = {evt => this.setState({email: evt.target.value})}/> 
                    <h3>Deadline</h3>
                    <input type = "date" className = "form-control" value = {this.state.deadline} onChange = {evt => this.setState({deadline: evt.target.value})}/>
                    <h3>Goal</h3>
                    <span className = "d-flex">
                        <input type = "number" className = "form-control" value = {this.state.goal} onChange = {evt => this.setState({goal: evt.target.value})}/> 
                        <select className = "form-select mt-1">
                            <option selected>Currency</option>
                            <option value = "ETH">ETH</option>
                            <option value = "DAI">DAI</option>
                            <option value = "ENJ">ENJ</option>
                            <option value = "SAND" >SAND</option>
                        </select>
                    </span>
                    <button className = "btn btn-success">Add</button>
                    <h3>Category</h3>
                    <select className = "form-select" aria-label = "Default select example" onChange = {e => this.setState({selectionModel:e.target.value})}>
                        <option selected>Pick a category for your campaign</option>
                        <option value = "health">Health</option>
                        <option value = "education">Education</option>
                        <option value = "last">Last wish</option>
                        <option value = "ideas">Ideas</option>
                    </select>
                </form>
            </Fragment>
        )
    }
};

export default InputCampaign;
