import React, { Component } from 'react';
import axios from 'axios';
import Web3 from 'web3';
import SupportChildren from '../abis/SupportChildren.json'
import './App.css';
import ReactDOM from "react-dom";
import ProgressBar from 'react-bootstrap/ProgressBar';
import ipfs from "../ipfs";
import emailjs from "emailjs-com";

class App extends Component {

  fileSelectedHandler = event => {
    this.setState({
      selectedFile: event.target.files[0]
    })
    this.captureFile(event)
  }

  fileUploadHandler = () => {
    const formData = new FormData();
    formData.append("file", this.state.selectedFile)
    formData.append("upload_preset", "nftauction")
    this.onSubmit()
    axios.post("https://api.cloudinary.com/v1_1/tothemoon/image/upload", formData, {
      onUploadProgress: progressEvent => {
        this.setState({
          prog : progressEvent.loaded/progressEvent.total * 100
        })
      }
    })
    .then(res => {
      this.setState({
        img: res.data.secure_url,
      })
      document.getElementById('fileChoose').style = "background-image: url('" + res.data.secure_url + "'); background-repeat: no-repeat; background-size: cover";
      document.getElementById('fileChoose').style.color = "rgba(0, 0, 0, 0)";
    })
  }

  async componentWillMount() {
    await this.loadWeb3()
      await this.loadBlockchainData()
  }

  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
    }
  }

  async loadBlockchainData() {
    const web3 = window.web3
    // Load account
    const accounts = await web3.eth.getAccounts()
    this.setState({account: accounts[0]})

    const networkId = await web3.eth.net.getId()
    const networkData = SupportChildren.networks[networkId]
    if(networkData) {
      const abi = SupportChildren.abi
      const address = networkData.address
      const contract = new web3.eth.Contract(abi, address)
      this.setState({ contract: contract})


    } else {
      window.alert('Smart contract not deployed to detected network')
    }

  }

  constructor(props){
    super(props)
    this.state = {
      name: '',
      desc: '',
      goal: '',
      date: '',
      email: '',
      img: '',
      prog:0,
      contract: null,
      account: '',

      ipfsHash: '',
      buffer: '',
      ethAddress: '',
      blockNumber: '',
      transactionHash: '',
      gasUsed: '',
      txReceipt: '',


    }
  }



  captureFile = (event) => {
      event.stopPropagation()
      event.preventDefault()
      const file = event.target.files[0]
      let reader = new window.FileReader()
      reader.readAsArrayBuffer(file)
      reader.onloadend = () => this.convertToBuffer(reader)
  };

  convertToBuffer = async (reader) => {
      //file is converted to a buffer for upload to IPFS
      const buffer = await Buffer.from(reader.result);
      //set this buffer -using es6 syntax
      this.setState({buffer});
  };

  onSubmit = async () => {
    await ipfs.add(this.state.buffer, (err, ipfsHash) => {

        this.setState({ipfsHash: ipfsHash[0].hash});
    }) 
  }; 

  createCampaign = (evt) => {
    evt.preventDefault()
    evt.persist()
    var timestamp = Date.parse(this.state.date)
    timestamp/=1000
    this.state.contract.methods.createCampaign(this.state.desc, this.state.date, "ne znam sta staviti ovde")
    .send({from: this.state.account}).then(() => {
      evt.preventDefault();

      emailjs.sendForm('service_7ksffb7', 'template_6x5plw4', evt.target, 'user_2AtKMM5Ljcoy0GZ33Dhia')
          .then((result) => {
            console.log(result.text);
            const element = document.getElementById("alert");
            const check = document.createElement('p');
            check.innerHTML = "You have successfully created a campaign, good luck!";
            check.style = "color: #00FF00; font-size: 22px;"
            element.appendChild(check);
          }, (error) => {
            console.log(error.text);
          });

    })
  }

  updateNameValue(evt) {
    this.setState({
      name: evt.target.value,
    });
  }

  updateDescValue(evt) {
    this.setState({
      desc: evt.target.value
    });
  }

  updateCampaignGoal(evt) {
    this.setState({
      goal: evt.target.value
    });
  }

  updateDate(evt) {
    this.setState({
      date: evt.target.value
    })
  }

  updateEmail(evt) {
    this.setState({
      email: evt.target.value
    })
  }


  render() {
    return (
        <div>
          <form>
            <input type = "date" onChange = {evt => this.updateDate(evt)} style = {{border: "1px solid black"}}/>
            <br/>
            <input type = "text" onChange = {evt => this.updateDescValue(evt)} style = {{border: "1px solid black"}} />
            <button type = "submit" onClick = {evt => this.createCampaign(evt)}>kreiraj kampanju</button>
          </form>
          
        </div>
    );
  }
}

export default App;
