import React, { Component } from 'react';
import Web3 from 'web3';
import SaveChildren from '../abis/SaveChildren.json'
import '../components/App.css';
import { Line } from 'react-chartjs-2';


class UserDashBoard extends Component{

    constructor(props){
        super(props)
        this.state = {
            contract: null,
            account: '',
            campaigns: []
        }
    }

    async componentWillMount() {
        await this.loadWeb3()

        await this.loadBlockchainData()
        await this.printUserCampaigns()
    }


    async loadWeb3() {
        if (window.ethereum) {
            window.web3 = new Web3(window.ethereum)
            await window.ethereum.enable()
        }
        else if (window.web3) {
            window.web3 = new Web3(window.web3.currentProvider)
        }
        else {
            window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
        }
    }


    async loadBlockchainData() {
        const web3 = window.web3
        // Load account
        const accounts = await web3.eth.getAccounts()
        this.setState({account: accounts[0]})

        const networkId = await web3.eth.net.getId()
        const networkData = SaveChildren.networks[networkId]
        if(networkData) {
            
            const abi = SaveChildren.abi
            const address = networkData.address
            const contract = new web3.eth.Contract(abi, address)
            this.setState({ contract: contract})

            const campaigns = await contract.methods.getCampaigns().call()
            this.setState({ campaigns: [...this.state.campaigns, campaigns]})
            console.log("Kampanje: ")
            console.log(this.state.campaigns)

        } else {
            window.alert('Smart contract not deployed to detected network')
        }
    }

    async printUserCampaigns(){
        const totalSupply = await this.state.contract.methods.getCampaignsLength().call()
        const mainDiv = document.getElementById("myDiv")

        for(var i =0;i<totalSupply;i++){
            const campaign = await this.state.contract.methods.campaigns(i).call()

            if(campaign.owner == this.state.account){
                const paragraf = document.createElement('p')
                paragraf.innerHTML = campaign.name

                mainDiv.appendChild(paragraf)
            }
        }
    }


    render() {
        return (
            <div id = "myDiv">
                <div>
                <Line
                    data = {{
                        labels: ['Red', 'Blue'],
                        datasets: [
                            {
                                label: '# of votes',
                                data: [12, 9]
                            }
                        ]
                    }}
                    height = {400}
                    width = {600}
                    options = {{
                        maintainAspectRatio: false,
                    }}
                />
                </div>
                
            </div>
        );
    }



}

export default UserDashBoard;
