import React, { Component } from 'react';
import Web3 from 'web3';
import SupportChildren from '../abis/SupportChildren.json'
import '../components/App.css';
import ReactDOM from "react-dom";
import emailjs from 'emailjs-com';
import NFT from '../abis/NFT.json';

class Campaign extends Component{

    constructor(props){
        super(props)
        this.state = {
            contract: null,
            contractNFT: null,
            account: '',
            campaign: '',
            amount: '',
            email: '',
            date: ''
        }
    }

    updateEmail(evt) {
        this.setState({
            email: evt.target.value
        })

    }

    async componentWillMount() {
        await this.loadWeb3()
        await this.loadBlockchainData()
        // await this.printDonors()
        await this.loadNFT()
    }


    async loadWeb3() {
        if (window.ethereum) {
            window.web3 = new Web3(window.ethereum)
            await window.ethereum.enable()
        }
        else if (window.web3) {
            window.web3 = new Web3(window.web3.currentProvider)
        }
        else {
            window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
        }
    }


    async loadBlockchainData() {
        const web3 = window.web3
        // Load account
        const accounts = await web3.eth.getAccounts()
        this.setState({account: accounts[0]})

        const networkId = await web3.eth.net.getId()
        const networkData = SupportChildren.networks[networkId]

        if(networkData) {
            const address = networkData.address
            const abi = SupportChildren.abi
            const contract = new web3.eth.Contract(abi, address)

            const lastItem = window.location.pathname.split("/").pop()
            const campaign = await contract.methods.campaigns(parseInt(lastItem)).call()


            this.setState({ 
                contract: contract, 
                campaign: campaign,
                date: campaign.timeCreated
            })

        } else {
            window.alert('Smart contract not deployed to detected network')
        }

    }

    // async printDonors(){
    //     const mainDiv = document.getElementById('donors')
    //     const len = await this.state.contract.methods.getDonorsLength(this.state.campaign.id).call()
    //     const donors = await this.state.contract.methods.getDonors(this.state.campaign.id).call()
    //     for(var i =0;i<Math.min(3, len);i++){
    //         const donation = donors[i]
    //         const li = document.createElement('li')

    //         const image = document.createElement('img')
    //         image.src = "https://cdn.discordapp.com/attachments/837326649074516048/867532401155899421/ethlogo.png"

    //         const p = document.createElement('p')
    //         p.innerHTML = donation.amount/(10**18) + " ETH"

    //         const h = document.createElement('h3')
    //         h.innerHTML = donation.from

    //         li.appendChild(image)
    //         li.appendChild(p)
    //         li.appendChild(h)
    //         mainDiv.appendChild(li)
    //     }

    // }

    async loadNFT(){
        const web3 = window.web3
        // Load account
        const accounts = await web3.eth.getAccounts()

        const networkId = await web3.eth.net.getId()
        const networkData = NFT.networks[networkId]

        if(networkData) {
            const address = networkData.address
            const abiNFT = NFT.abi
            const contractNFT = new web3.eth.Contract(abiNFT, address)

            const rec = await contractNFT.methods.print().call()

            this.setState({ 
                contractNFT: contractNFT
            })

        } else {
            window.alert('Smart contract not deployed to detected network')
        }

    }

    upadateAmount(evt) {
        this.setState({
          amount: evt.target.value
        });
    }

    updateERCAmount(evt){
        this.setState({
            ercAmount: evt.target.value
        });
    }
    sendEmail(e) {
        e.preventDefault();

    }

    // donate = () => {
    //     this.state.contract.methods.donate(parseInt(this.state.campaign.id), this.state.email)
    //     .send({from: this.state.account, value: parseFloat(this.state.amount)*(10**18)}).once('receipt', (receipt) => {
    //         this.mintNFT(JSON.stringify(receipt))
    //     })
    //     .then(() => {
    //         var templateParams = {
    //             user_email: this.state.email,
    //             donation_amount: this.state.amount,
    //             campaign_name: this.state.campaign.name,
    //         };
    //     emailjs.send('service_7ksffb7', 'template_4nal1hg', templateParams, 'user_2AtKMM5Ljcoy0GZ33Dhia')
    //         .then(function(response) {
    //             console.log('SUCCESS!', response.status, response.text);
    //         }, function(error) {
    //             console.log('FAILED...', error);
    //         });
    //         var amount1 = parseFloat(this.state.amount) * (10**18)
    //         if((parseInt(this.state.campaign.raised)-amount1) < parseInt(this.state.campaign.goal) && (parseInt(this.state.campaign.raised) + amount1) >= parseInt(this.state.campaign.goal)){
    //             this.alertEveryone()
    //         }

    //     })
    // }

    donate = () => {
        this.state.contract.methods.donate(0, this.state.tokenAddress, this.state.ercAmount).send({from: this.state.account})
    }

    donateETH = () => {
        this.state.contract.methods.donateETH(0).send({from: this.state.account, value: parseFloat(this.state.amount)*(10**18)})
    }

    async alertEveryone(){
        const mails = await this.state.contract.methods.getMailsById(this.state.campaign.id).call()
        const mailsSet = [...new Set(mails)]
        const len = mails.length
        for(var i =0;i<len;i++){
            var templateParams = {
                user_email: mailsSet[i],
                campaign_name: this.state.campaign.name
            };
            emailjs.send('service_51ifv2q', 'template_3ai231s', templateParams, 'user_AzzxvRUXgtcUIpvSjfoOA')
            .then(function(response){
                console.log("SUCCESS!", response.status, response.text);
            }, function(error){
                console.log('FAILED...', error);
            });

        }
    }

    mintNFT = (receipt) => {
        this.state.contractNFT.methods.mint(receipt).send({from: this.state.account})
    }


    render(){

        return(
            <div>
                <p>Donate ETH</p>
                <input placeholder= "AMOUNT" onChange = {evt => this.upadateAmount(evt)}></input>
                <button onClick = {this.donateETH} style = {{color: 'black', border: '1px solid black'}}>DONATE ETH</button>

                <p>Donate ERC20-Token</p>
                <input placeholder = "AMOUNT" onChange = {evt => this.updateERCAmount(evt)} style = {{width: "300px"}}></input>
                <button onClick = {this.donate} style = {{color: 'black', border: '1px solid black'}}>DONATE ERC</button>

            </div>
        );
    }
}

export default Campaign;
