import React, {Fragment, Component} from 'react';
import Web3 from 'web3';
import { MaticPOSClient } from '@maticnetwork/maticjs'
import WalletConnectProvider from "@maticnetwork/walletconnect-provider";


const MaticPoSClient = require("@maticnetwork/maticjs").MaticPOSClient;
const Network = require("@maticnetwork/meta/network");
const Matic = require("@maticnetwork/maticjs");


const config = require("../config");
const utilsPoly = require("../utilsPoly");


class L2View extends Component {

    constructor(props){
        super(props);
        this.state = {
            Networkid: 0,
            account: "",
            inputValue: "",
            burnHash: "",
            loading: true,
            maticProvider: null,
            ethereumProvider: null, 

        }
    }

    async execute() {
      const { matic, network } = await utilsPoly.getMaticClient()
      const { from } = utilsPoly.getAccount()
    
      const token = network.Main.Contracts.Tokens.MaticWeth
      const amount = matic.web3Client.web3.utils.toWei('1.567')
    
      // deposit
      await matic.depositEther(token, from, amount).then((res) => {
            console.log("deposit hash: ", res.transactionHash)
      })
    }
    

    render(){
        return(
            <div
            id="Ether"
            >
            <button
              onClick={this.depositEther}
              disabled={
                this.state.Networkid !== 0 && this.state.Networkid === config.l2.MATIC_CHAINID
                  ? true
                  : false
              }
            >
              Deposit
            </button>

            <button
              onClick={this.burnEther}
              disabled={
                this.state.Networkid !== 0 && this.state.Networkid === config.l2.ETHEREUM_CHAINID
                  ? true
                  : false
              }
            >
              burn
            </button>

            <button
              onClick={this.exitEther}
              disabled={
                this.state.Networkid !== 0 && this.state.Networkid === config.l2.ETHEREUM_CHAINID
                  ? false
                  : true
              }
            >
              exit
            </button>

            <br />
            <input
              id="inputValue"
              type="text"
              placeholder="value"
              name="inputValue"
              value={this.state.inputValue}
              onChange={this.onchange}
              required
            />
            <p id="burnHash">{this.state.burnHash}</p>
            <button onClick = {this.execute}></button>
          </div>
        );
    }

}

export default L2View;
