import React, {Fragment, useState, useEffect, Component} from 'react';
import logo from './logo.svg';
import Web3 from 'web3';
import SupportChildren from "./abis/SupportChildren.json";
import './App.css';

import InputCampaign from './components/InputCampaign';
import ListCampaign from './components/ListCampaign';

class App extends Component{

  constructor(props){
    super(props)
    this.state = {
      contract: null,
      account: ''
    }
  }

  async componentWillMount() {
    await this.loadWeb3();
    await this.loadBlockchainData();
  } 

  async loadWeb3 () {
    if(window.ethereum){
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }else if(window.web3){
      window.web3 = new Web3(window.web3.currentProvider)
    }else{
      window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
    }
  }

  async loadBlockchainData() {
    const web3 = window.web3
    // Load account
    const accounts = await web3.eth.getAccounts()

    const networkId = await web3.eth.net.getId()
    const networkData = await SupportChildren.networks[4]
    if(networkData) {
        const abi = SupportChildren.abi
        const address = networkData.address
        const contract = new web3.eth.Contract(abi, address)

        this.setState({
          contract: contract,
          account: accounts
        })
        console.log(this.state.contract)
    } else {
        window.alert('Smart contract not deployed to detected network')
    }
  }

  // useEffect(() => {
  //   componentWillMount();
  // }, [])
    render(){
    return (
      <Fragment>
        <div className = "container">
          <InputCampaign/>
          <ListCampaign contract = {this.state.contract} account = {this.state.account}/>
        </div>
      </Fragment>
    );
    }
}

export default App;
