import React, { Component } from 'react';
import Web3 from 'web3';
import SaveChildren from '../abis/SaveChildren.json'
import '../App.css';
import profimg from '../images/profileimage.png';
import donoapp from '../images/donoapp.png';
import { Line } from 'react-chartjs-2';


class UserDashBoard extends Component{

    constructor(props){
        super(props)
        this.state = {
            contract: null,
            account: '',
            campaigns: [],
            labels: [],
            data: []
        }
    }

    async componentWillMount() {
        await this.loadWeb3()
        await this.loadBlockchainData()
    }


    async loadWeb3() {
        if (window.ethereum) {
            window.web3 = new Web3(window.ethereum)
            await window.ethereum.enable()
        }
        else if (window.web3) {
            window.web3 = new Web3(window.web3.currentProvider)
        }
        else {
            window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
        }
    }


    async loadBlockchainData() {
        const web3 = window.web3
        // Load account
        const accounts = await web3.eth.getAccounts()
        this.setState({account: accounts[0]})

        const networkId = await web3.eth.net.getId()
        const networkData = SaveChildren.networks[networkId]
        if(networkData) {
            
            const abi = SaveChildren.abi
            const address = networkData.address
            const contract = new web3.eth.Contract(abi, address)
            this.setState({ contract: contract})

            const campaigns = await contract.methods.getCampaigns().call()
            this.setState({ campaigns: [...this.state.campaigns, campaigns]})

        } else {
            window.alert('Smart contract not deployed to detected network')
        }
    }


    render() {
        return (
            <div>
              <title>My profile</title>
              <div className="dist1">
                <div className = "content" style={{padding: "300px"}}>
                  <div className="heading">
                    <a>Donations</a>
                    <button className="buttonfund" type="button" style={{float: 'right'}} onclick="#">Create fundraise</button>
                  </div>
                  <div className="nekosranje" />
                  <div className="heading">
                    <a>Fundraise</a>
                  </div>
                  <div className="acinac">
                    <a>Active</a>
                    <a className="breck">(0)</a>
                  </div>
                  <div className="nekosranje" />
                  <div className="acinac">
                    <a>Inactive</a>
                    <a className="breck">(0)</a>
                  </div>
                  <div className="nekosranje" />
                </div>
              </div>
            </div>
          );
    }



}

export default UserDashBoard;
