import React, {Fragment, useState} from 'react';

//@author Luka

const InputCampaign = () => {

    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [email, setEmail] = useState("");
    const [url, setUrl] = useState("url slike");
    const [deadline, setDeadline] = useState("");
    const [goal, setGoal] = useState("");

    const onSubmitForm = async (evt) => {
        evt.preventDefault();
        try {
            const body = {title, description, email, url, deadline, goal};
            console.log(body);
            const response = await fetch("http://localhost:5000/create-campaign", {
                method: "POST",
                headers: {"Content-Type": "application/json"},
                body: JSON.stringify(body)
            });

            window.location = "/";
        } catch (error) {
            console.log(error.message);
        }
    }

    return(
        <Fragment>
            <h1 className = "text-center mt-5">Create a campaign</h1>
            <form className = "d-block mt-5" onSubmit = {onSubmitForm}>
                <h3>Title</h3>
                <input type = "text" className = "form-control" value = {title} onChange = {evt => setTitle(evt.target.value)}/>
                <h3>Description</h3>
                <input type = "text" className = "form-control" value = {description} onChange = {evt => setDescription(evt.target.value)}/>
                <h3>Email</h3>
                <input type = "text" className = "form-control" value = {email} onChange = {evt => setEmail(evt.target.value)}/> 
                <h3>Deadline</h3>
                <input type = "date" className = "form-control" value = {deadline} onChange = {evt => setDeadline(evt.target.value)}/>
                <h3>Goal</h3>
                <input type = "number" className = "form-control" value = {goal} onChange = {evt => setGoal(evt.target.value)}/> 
                <button className = "btn btn-success">Add</button>
            </form>
        </Fragment>
    )
};

export default InputCampaign;
